import React, { useEffect, useState } from 'react';
import { 
  View,
  Text,
  FlatList,
  TouchableWithoutFeedback,
  TouchableHighlight,
  Switch,
  StyleSheet
} from 'react-native';
import { 
  widthPercentageToDP as wp,
  heightPercentageToDP as hp
} from 'react-native-responsive-screen';

const Board = ({ data, setData, turn, setTurn, disabled, setDisabled, ai, setAi }) => {

  /*
    Props
    data => 2d array of 6 rows and 7 columns representing the state of the connect4 game board
    setData => Method to update the data prop
    turn => Integer representing whose turn it is, i.e., player 1, player 2, or AI
    setTurn => Method to update the turn prop
    disabled => Boolean to enable or disable the connect4 game board. Board is disabled once the game is over
    setDisabled => Method to update the disabled prop
    ai => Boolean representing whether the AI is playing or not. If false, it means player 2 is playing
    setAi => Method to update the ai prop
  */
  const [showAlert, setShowAlert] = useState(false)

  useEffect(() => {
    // Write Task 11 code below this line
  }, []);

  const styles = StyleSheet.create({
    buttonWrapper: {
      display: 'flex',
      flexDirection: 'row',
      alignItems: 'center',
      justifyContent: 'space-evenly',
      rowGap: wp(10),
      margin: wp(4),
    },
    restartButton: {
      height: hp(6),
      width: wp(20),
      fontSize: wp(5),
    },
    textTitle: {
        fontSize: wp(6),
        fontWeight: 500,
        color: '#268ec7',
    },
    boardContainer: {
      height: 300,
      width: 350,
      backgroundColor: 'blue'
    },
    disc: {
      height: 50,
      width: 50,
      borderRadius: wp(50)
    },
    row: {
      flexDirection: 'row',
      flex: 1,
    },
  })

  /*
    Parameters
    board => 2d array of 6 rows and 7 columns representing the connect4 game board
  */
  const checkAvailableColumns = (board) => {
    const emptyCols = []
    for(let row=0; row<6; row++){
      for(let col=0; col<7; col++){
        if(board[row][col] == null && !emptyCols.includes(col)) {
          emptyCols.push(col)
        }
      }
    }
    return emptyCols
  }

  /*
    Parameters
    column => Integer between 0 and 6 (inclusive) representing column index of the connect4 game board
    board => 2d array of 6 rows and 7 columns representing the connect4 game board
  */
  const getEmptyRow = (column, board) => {
    // Write Task 5 code below this line
    for(let row=0; row<6; row++){
      if(board[row][column] == null){
        return row
      }
    }
    return null
  }

  /*
    Parameters
    emptyRow => Integer representing row index having a value of null where disc can be placed
    column => Integer between 0 and 6 (inclusive) representing column index of the connect4 game board
    player => Integer representing 1 for player 1 and 2 for player 2 or AI
  */
  const placeDisc = (emptyRow, column, player) => {
    const newData = [...prev]
    newData[emptyRow][column] = player
    setData(newData)
  }

  /*
    Parameters
    board => 2d array of 6 rows and 7 columns representing the connect4 game board
    player => Integer representing 1 for player 1 and 2 for player 2 or AI
  */
  const checkWin = (board, player) => {
    // Write Task 6 code below this line
    for(let row=0; row<6; row++){
      for(let col=0; col<7; col++){
        if(col<4 && board[row][col] === board[row][col+1] === board[row][col+2] === board[row][col+3] === player){
          return true
        }
        if(row<3 && board[row][col] === board[row+1][col] === board[row+2][col] === board[row+3][col]=== player){
          return true
        }
        if(row<3 && col<4 && board[row][col] === board[row+1][col+1] === board[row+2][col+2] === board[row+3][col+3] === player){
          return true
        }
        if(row>2 && col>2 && board[row][col] === board[row-1][col-1] === board[row-2][col-2] === board[row-3][col-3] === player){
          return true
        }
      }
    }
    return false
  }

  /*
    Parameters
    column => Integer between 0 and 6 (inclusive) representing column index of the connect4 game board
  */
  const takeTurn = (column) => {
    console.log('click on ', column)
    // Write Task 7 code below this line
    const availableCols = checkAvailableColumns(data)
    if(!availableCols.includes(column)){
      window.alert("This column is full! Select another one.")
      return
    } 
    const emptyRow = getEmptyRow
    if(emptyRow < 6){
      placeDisc(emptyRow, column, turn)
    }
    if(checkWin(data, turn)) {
      setDisabled(true)
      return
    }
    if(checkAvailableColumns(data).length == 0){
      setTurn(0)
      setDisabled(true)
      return
    } else{
      setTurn(turn == 1 ? 2 : 1)
    }

  }

  const restartGame = () => {
    // Write Task 8 code below this line
    const newData = Array(6).fill(null).map(() => Array(7).fill(null));
    setData(newData);
    setTurn(1);
    setDisabled(false);
  }

  /*
    Parameters
    window => An array of length 4 whose indices can have one of the following values: 1, 2, and null
  */
  const getWindowScore = (window) => {
    // Write Task 9 code below this line
    let score = 0
    if(ai) {
      window.map((item) => {
        score = item === 2 ? score + 1 : score
      })
    }
    return score
  }

  /*
    Parameters
    board => 2d array of 6 rows and 7 columns representing the connect4 game board
  */
  const evaluate = (board) => {
    // let score = 0
    // for(let row=0; row<6; row++){
    //   for(let column=0; column<7; column++){
    //     //horizontal
    //     if(column<4){

    //     }
    //   }
    // }

    const aiPiece = 2;
    let score = 0;
    // Center column score check
    const centerColumn = 3;
    let centerAiPieces = 0;
    for (let row = 0; row <= 5; row++) {
      if (board[row][centerColumn] == aiPiece)
        centerAiPieces++;
    }
    score += (centerAiPieces * 3);
    for (let row = 0; row <= 5; row++) {
      for (let column = 0; column <= 6; column++) {
        // Horizontal score check
        if (column <= 3) {
          const horizontalWindow = [board[row][column], board[row][column+1], board[row][column+2], board[row][column+3]];
          score += getWindowScore(horizontalWindow);
        }
        // Vertical score check
        if (row <= 2) {
          const verticalWindow = [board[row][column], board[row+1][column], board[row+2][column], board[row+3][column]];
          score += getWindowScore(verticalWindow);
        }
        // Positive diagonals score check
        if (row >= 3 && column <= 3) {
          const diagonalWindow = [board[row][column], board[row-1][column+1], board[row-2][column+2], board[row-3][column+3]];
          score += getWindowScore(diagonalWindow);
        }
        // Negative diagonals score check
        if (row >= 3 && column >= 3) {
          const diagonalWindow = [board[row][column], board[row-1][column-1], board[row-2][column-2], board[row-3][column-3]];
          score += getWindowScore(diagonalWindow);
        }
      }
    }
    return score;
  }

  /*
    Parameters
    board => 2d array of 6 rows and 7 columns representing the connect4 game board
    depth => Integer representing the recursion depth. Can have values 1 and greater than 1
    maximizingPlayer => Boolean value representing whether the player is maximizing or not
  */
  const minimax = (board, depth, maximizingPlayer) => {
    // Write Task 10 code below this line
  }

  const aiTurn = () => {
    // Write Task 11 code below this line
  }

  // Write Task 2 and Task 3 code below this line

  const renderItem = ({ item }) => (
    <View style={styles.row}>
      {item.map((cell, index) => (
        <TouchableWithoutFeedback disabled={disabled} onPress={() => takeTurn(index)} key={index}>
          <View key={index} style={[styles.disc, {backgroundColor: cell === 1 ? 'yellow' : cell === 2 ? 'red' : 'white' }]} />
        </TouchableWithoutFeedback>

      ))}
    </View>
  );

  return (
    <View>
      <div style={styles.buttonWrapper}>
      <button style={styles.restartButton} onClick={()=>restartGame()}>Restart</button>
      <Text style={styles.textTitle}>AI</Text> 
      <Switch 
        value={ai} 
        onValueChange={() => {
          setAi(!ai)
          if(ai){
            restartGame()
            window.alert("AI enabled! Game restarted. Now, an AI will play as the opponent.")
          } else{
            restartGame()
            window.alert("AI disabled! Game restarted. Now, a human will play as the opponent.")
          }
        }} 
      />
      </div>

      <div style={styles.boardContainer}>

      <FlatList 
        data={data}
        renderItem={renderItem}
        keyExtractor={(_, index) => `row_${index}_column_${index}`}
      />
      </div>

    </View>
  );
};

export default Board;
