import React from 'react';
import { View, Text, StyleSheet } from 'react-native';
import { widthPercentageToDP as wp, heightPercentageToDP as hp } from 'react-native-responsive-screen';

const Turn = ({ turn, disabled, ai }) => {

  /*
    Props
    turn => Integer representing whose turn it is, i.e., player 1, player 2, or AI
    disabled => Boolean to enable or disable the connect4 game board. Board is disabled once the game is over
    ai => Boolean representing whether the AI is playing or not. If false, it means player 2 is playing
  */

  // Write Task 4 code below this line

  const GameOutcome = () => {
    if(disabled && turn === 0){
      return <Text>It is a draw!</Text>
    }
    if(disabled && !ai){
      if(turn === 1){
        return <Text>Player 1 won!</Text>
      }else if(turn === 2){
        return <Text>Player 2 won!</Text>
      }
    }
    if(disabled && ai){
      if(turn === 1){
        return <Text>Player 1 won!</Text>
      }else if(turn === 2){
        return <Text>AI won!</Text>
      }
    }
  }

  return (
    <>
    {!disabled && !ai && (<Text>
      {turn === 1 ? `Turn: Player 1` : `Turn: Player 2`}
    </Text>)}
    {!disabled && ai && (<Text>
      {turn === 1 ? `Turn: Player 1` : `Turn: AI`}
    </Text>)}
    <GameOutcome />
    </>
  );
};

export default Turn;
