import React from 'react';
import { View, Text, StyleSheet } from 'react-native';
import { widthPercentageToDP as wp, heightPercentageToDP as hp } from 'react-native-responsive-screen';

const Turn = ({ turn, disabled, ai }) => {
  return (
    <View style={styles.infoView}>
      {disabled ? (
        turn == 0 ? (
          <Text style={styles.turn}>It is a draw!</Text>
        ) : ai ? (
          turn == 1 ? (
            <>
              <Text style={styles.turn}>Player {turn} </Text>
              <View style={[styles.turnCircle, { marginRight: wp(3) }]}></View>
              <Text style={styles.turn}>won!</Text>
            </>
          ) : (
            <>
              <Text style={styles.turn}>AI </Text>
              <View style={[styles.turnCircle, {backgroundColor: '#b11d22', marginRight: wp(3)}]}></View>
              <Text style={styles.turn}>won!</Text>
            </>
          )
        ) : (
          <>
            <Text style={styles.turn}>Player {turn} </Text>
            {turn == 1 ? (
              <View style={[styles.turnCircle, { marginRight: wp(3) }]}></View>
            ) : (
              <View style={[styles.turnCircle, {backgroundColor: '#b11d22', marginRight: wp(3)}]}></View>
            )}
            <Text style={styles.turn}>won!</Text>
          </>
        )
      ) : (
        <>
          <Text style={styles.turn}>
            {ai && turn == 2 ? 'Turn: AI ' : `Turn: Player ${turn} `}
          </Text>
          {turn == 1 ? (
            <View style={styles.turnCircle}></View>
          ) : (
            <View style={[styles.turnCircle, { backgroundColor: '#b11d22' }]}></View>
          )}
        </>
      )}
    </View>
  );
};

const styles = StyleSheet.create({
  infoView: {
    flex: 2,
    alignItems: "flex-start",
    justifyContent: 'center',
    flexDirection: 'row',
    paddingTop: wp(6),
  },
  turn: {
    color: 'black',
    fontSize: wp(12),
    fontFamily: 'notoserif',
    fontWeight: '300',
  },
  turnCircle: {
    width: wp(12),
    height: hp(6),
    borderRadius: wp(15),
    backgroundColor: '#ccb002',
    marginTop: wp(2.5),
    marginRight: wp(5)
  },
});

export default Turn;
